&#x27;; while (n) &#123; if (n &amp; 1) &#123; n -= 3; std::cout &lt;&lt; 3 &lt;&lt; &quot; 
&quot;[n == 0]; &#125; else &#123; n -= 2; std::cout &lt;&lt; 2 &lt;&lt; &quot; 
&quot;[n == 0]; &#125; &#125;&#125;int main() &#123; int numTest; std::cin &gt;&gt; numTest; while (numTest--) &#123; solve(); &#125; return 0;&#125; B 「Come Together」 这个也没啥好说的，只需要看 B、C 和 A 的相对位置然后分类讨论一下即可。 时间复杂度：\(O(1)\) 空间复杂度：\(O(1)\) 123456789101112131415161718192021222324252627282930313233343536373839404142434445#include &lt;iostream&gt;#include &lt;cmath&gt;using i64 = long long;void solve() &#123; int xa, ya, xb, yb, xc, yc; std::cin &gt;&gt; xa &gt;&gt; ya &gt;&gt; xb &gt;&gt; yb &gt;&gt; xc &gt;&gt; yc; xb -= xa, xc -= xa; yb -= ya, yc -= ya; int res = 1; bool same_x = (i64)xb * xc &gt;= 0; bool same_y = (i64)yb * yc &gt;= 0; if (same_x) &#123; // xb xc 同号 if (same_y) &#123; res += std::min(abs(xb), abs(xc)) + std::min(abs(yb), abs(yc)); &#125; else &#123; res += std::min(abs(xb), abs(xc)); &#125; &#125; else &#123; // xb xc 异号 if (same_y) &#123; res += std::min(abs(yb), abs(yc)); &#125; &#125; std::cout &lt;&lt; res &lt;&lt; &#x27;
&#x27;;&#125;int main() &#123; std::ios::sync_with_stdio(false); std::cin.tie(nullptr); std::cout.tie(nullptr); int numTest; std::cin &gt;&gt; numTest; while (numTest--) &#123; solve(); &#125; return 0;&#125; C 「Strong Password」 考虑贪心策略，每一次从限定条件中选取最晚在 database string 中出现的字符，这样可以尽可能快地消耗掉 database string 的前缀，如果中途出现了后缀中不存在的限定条件内的字符，则说明存在答案，反之说明不存在。 为了记录 database string 所有后缀中字符的出现时间，我们需要一个数组来维护每个后缀所有字符出现的最小索引。 时间复杂度：\(O(nm)\) 空间复杂度：\(O(nm)\) 12345678910111213141516171819202122232425262728293031323334353637383940414243444546474849#include &lt;iostream&gt;#include &lt;vector&gt;#define INF 0x7f7f7f7fvoid solve() &#123; std::string dat; std::cin &gt;&gt; dat; int n; std::cin &gt;&gt; n; std::string l, r; std::cin &gt;&gt; l &gt;&gt; r; int len = dat.size(); // 记录区间 [i, len - 1] 中每个数出现的最早下标 std::vector&lt;std::vector&lt;int&gt;&gt; nextIdx(len + 1); nextIdx.back() = std::vector&lt;int&gt;(10, INF); for (int i = len - 1; i &gt;= 0; i--) &#123; nextIdx[i] = nextIdx[i + 1]; nextIdx[i][dat[i] - &#x27;0&#x27;] = i; &#125; int p = 0; for (int i = 0; i &lt; n; i++) &#123; p = *std::max_element(nextIdx[p].begin() + l[i] - &#x27;0&#x27;, nextIdx[p].begin() + r[i] - &#x27;0&#x27; + 1) + 1; // 找到最晚出现的数字 if (p &gt;= INF) &#123; // 出现无法找到的数字 std::cout &lt;&lt; &quot;YES
&quot;; return; &#125; &#125; std::cout &lt;&lt; &quot;NO
&quot;;&#125;int main() &#123; std::ios::sync_with_stdio(false); std::cin.tie(nullptr); std::cout.tie(nullptr); int numTest; std::cin &gt;&gt; numTest; while (numTest--) &#123; solve(); &#125; return 0;&#125;

比赛链接 这一场打得一般，因为太困了。😢 A 「Tenzing and Tsondu」 水题，易证总和高的人必胜，总和相等则平局。 时间复杂度：\(O(n)\) 空间复杂度：\(O(1)\) 12345678910111213141516171819202122232425262728293031323334353637383940#include &lt;iostream&gt;using i64 = long long;void solve() &#123; int n, m; std::cin &gt;&gt; n &gt;&gt; m; i64 sum_1 = 0, sum_2 = 0; for (int i = 0, x; i &lt; n; i++) &#123; std::cin &gt;&gt; x; sum_1 += x; &#125; for (int i = 0, x; i &lt; m; i++) &#123; std::cin &gt;&gt; x; sum_2 += x; &#125; if (sum_1 &gt; sum_2) &#123; std::cout &lt;&lt; &quot;Tsondu
&quot;; &#125; else if (sum_1 &lt; sum_2) &#123; std::cout &lt;&lt; &quot;Tenzing
&quot;; &#125; else &#123; std::cout &lt;&lt; &quot;Draw
&quot;; &#125;&#125;int main() &#123; std::ios::sync_with_stdio(false); std::cin.tie(nullptr); std::cout.tie(nullptr); int numTest; std::cin &gt;&gt; numTest; while (numTest--) &#123; solve(); &#125; return 0;&#125; B 「Tenzing and Books」 根据题意，如果某本书的 knowledge 某个 bit 位为 1 而 x 在该位为 0，即满足 knowledge &amp; ~x 非 0，则该书一定不能选取，根据栈的性质，该书以下的书也不能选取。 而或运算对于 3 个栈的操作顺序本身而言没有影响，所以我们依次遍历每个栈即可，如果遍历过程中出现不能选择的书，那么就结束该轮循环，进入下一个栈，3 轮循环中如果出现某个状态等于 x ，则说明可以使得 knowledge 的值等于 x ，若 3 轮循环结束后都没有得到目标答案，则说明无法使得 knowledge 的值等于 x 。 时间复杂度：\(O(n)\) 空间复杂度：\(O(n)\) 12345678910111213141516171819202122232425262728293031323334353637383940414243444546#include &lt;iostream&gt;#define MAX_N 100005int stk[3][MAX_N];void solve() &#123; int n, x; std::cin &gt;&gt; n &gt;&gt; x; for (int i = 0; i &lt; 3; i++) &#123; for (int j = 0; j &lt; n; j++) &#123; std::cin &gt;&gt; stk[i][j]; &#125; &#125; int mask = ~x, ans = 0; if (x == 0) &#123; std::cout &lt;&lt; &quot;Yes
&quot;; return; &#125; for (int i = 0; i &lt; 3; i++) &#123; for (int j = 0; j &lt; n; j++) &#123; if (stk[i][j] &amp; mask) break; ans |= stk[i][j]; if (ans == x) &#123; std::cout &lt;&lt; &quot;Yes
&quot;; return; &#125; &#125; &#125; std::cout &lt;&lt; &quot;No
&quot;;&#125;int main() &#123; std::ios::sync_with_stdio(false); std::cin.tie(nullptr); std::cout.tie(nullptr); int numTest; std::cin &gt;&gt; numTest; while (numTest--) &#123; solve(); &#125; return 0;&#125; C 「Tenzing and Balls」 若存在 \(i、 j、 m、 n\) 使得 \(a_i = a_j\) 且 \(a_m = a_n\) ，则考虑区间 \([i, j]\) 和 \([m, n]\) ： 若两个区间无交集，则两个区间都可以删除且互不影响； 若两个区间是包含关系，不妨设 \([i, j] \subsetneq [m, n]\)，则 \([i, j]\) 只能在 \([m, n]\) 之前删除，而删除 \([i, j]\) 再删除 \([m, n]\) 与直接删除 \([m, n]\) 的效果是一样的； 若两个区间相交且不为包含关系，则只能删除其中一个区间。 综上所述，问题就变成了如何选取不相交的区间，使得选取的区间总长度最长。 我们假设 \(dp[i]\) 表示区间 \([0, i]\) 删除完后剩下来的数的最小值，\(dp[0]\) 初始化为 1，我们可以得到以下关系： 若不删除 \(a_i\) ，则 \(dp[i] = dp[i - 1] + 1\) 若能删除 \(a_i\) ，则 \(dp[i] = \underset{j}{min}\ \{dp[j]\ |\ j &lt; i - 1 \land a_{j + 1} = a_i \}\) 于是，我们就得到了状态转移方程：\(dp[i] = min\{dp[i - 1] + 1,\ \underset{j}{min}\ \{dp[j]\ |\ j &lt; i - 1 \land a_{j + 1} = a_i \}\) 而对于 \(\underset{j}{min}\ \{dp[j]\ |\ j &lt; i - 1 \land a_{j + 1} = a_i \}\) 这一项，我们考虑使用一个数组更新每轮迭代后满足 \(a_{j + 1} = a_i\) 的最小 \(dp[j]\) 的值即可。迭代完后 \(n - dp[n - 1]\) 即为可删除的最大数量。 时间复杂度：\(O(n)\) 空间复杂度：\(O(n)\) 1234567891011121314151617181920212223242526272829303132333435363738394041#include &lt;iostream&gt;#include &lt;cstring&gt;#define MAX_N 200005int arr[MAX_N];int dp[MAX_N]; // 最少剩多少个int memo[MAX_N]; // memo[t] 表示满足 arr[j + 1] == t 的所有数里面 dp[j] 的最小值void solve() &#123; int n; std::cin &gt;&gt; n; for (int i = 0; i &lt; n; i++) &#123; std::cin &gt;&gt; arr[i]; dp[i] = i + 1; &#125; memset(memo + 1, 0x7f, n * sizeof(int)); memo[arr[0]] = 0; for (int i = 1; i &lt; n; i++) &#123; dp[i] = std::min(dp[i - 1] + 1, memo[arr[i]]); memo[arr[i]] = std::min(memo[arr[i]], dp[i - 1]); &#125; std::cout &lt;&lt; n - dp[n - 1] &lt;&lt; &#x27;
&#x27;;&#125;int main() &#123; std::ios::sync_with_stdio(false); std::cin.tie(nullptr); std::cout.tie(nullptr); int numTest; std::cin &gt;&gt; numTest; while (numTest--) &#123; solve(); &#125; return 0;&#125; D 「Tenzing and His Animal Friends」 题目有点抽象，大概就是说全部轮游戏中 \(u_i\) 和 \(v_i\) 的分开游戏时间总和均不超过 \(y_i\)，即 \(u_i\) 不和 \(v_i\) 玩的游玩时间不超过 \(y_i\) ，对 \(v_i\) 也是同理。于此同时，顶点 1 每轮游戏都要参加，顶点 n 每轮游戏都不能参加。 我们接下来将所有的限制看成一个无向图，若存在限制 \((u_i, v_i, y_i)\)，那么就在顶点 \(u_i\) 和 \(v_i\) 之间连接一条权重为 \(y_i\) 的边。 考虑以下两种情况： 若顶点 1 无法在有限步内到顶点 n，就说明顶点 1 和顶点 n 属于不同的分支，那我们每次可以和顶点 1 所在分支全部顶点一起玩，且没有时间限制。此时答案为 inf 。 若顶点 1 可以到达顶点 n，我们假设其中一条路径为 \(&lt;1, k_1, k_2, ..., k_m, n&gt;\) ，每个顶点的游玩时间为 \(t_1, t_{k_1}, t_{k_2}, ..., t_{k_m}, t_n\)。 因为顶点 1 每轮游戏都参与，所以顶点 1 游玩的时间就是总的游戏时间，因此我们只要求出 \(t_1\) 的最小值即可。 由于一对顶点 \((x, y)\) 分开的时间不超过 \(d(x, y)\)，我们可以得到：\(t_x - t_y \leq d(x, y)\)。 所以有： \[  egin{array}{} t_1 - t_{k_1} \leq d(1, k_1) \ t_{k_1} - t_{k_2} \leq d(k_1, k_2) \ ... \ t_{k_m} - t_{n} \leq d(k_m, n) \end{array} \] 将上式求和： \[ \implies t_1 - t_n \leq d(1, k_1) + d(k_1, k_2) + ... + d(k_m, n) \] 又因为顶点 n 不参与游戏，所以 \(t_n = 0\)，综上所述： \[ t_1 \leq d(1, k_1) + d(k_1, k_2) + ... + d(k_m, n) \] 右式其实就是顶点 1 到顶点 n 的路径长度，换言之，游戏的时长必须小于等于所有顶点 1 到顶点 n 的路径长度。所以我们只要求出顶点 1 到顶点 n 到最短路径即可，考虑最小堆优化的 Dijkstra 算法，并用一个数组记录每次出队列的顶点。 时间复杂度：\(O(mlog(m + n))\) 空间复杂度：\(O(n + m)\) 1234567891011121314151617181920212223242526272829303132333435363738394041424344454647484950515253#include &lt;iostream&gt;#include &lt;vector&gt;#include &lt;queue&gt;using i64 = long long;void solve() &#123; int n, m; std::cin &gt;&gt; n &gt;&gt; m; std::vector&lt;std::vector&lt;std::pair&lt;int, int&gt;&gt;&gt; g(n); for (int i = 0, u, v, y; i &lt; m; i++) &#123; std::cin &gt;&gt; u &gt;&gt; v &gt;&gt; y; u--; v--; g[u].push_back(&#123;v, y&#125;); g[v].push_back(&#123;u, y&#125;); &#125; std::vector&lt;i64&gt; dist(n, -1); std::priority_queue&lt;std::pair&lt;i64, int&gt;, std::vector&lt;std::pair&lt;i64, int&gt;&gt;, std::greater&lt;&gt;&gt; q; q.push(&#123;0, 0&#125;); std::vector&lt;i64&gt; ans; while (!q.empty()) &#123; auto [d, to] = q.top(); q.pop(); if (dist[to] != -1) continue; dist[to] = d; ans.push_back(to); if (to == n - 1) break; for (auto [nxt, w] : g[to]) &#123; q.push(&#123;d + w, nxt&#125;); &#125; &#125; if (dist[n - 1] == -1) &#123; std::cout &lt;&lt; &quot;inf
&quot;; return; &#125; std::cout &lt;&lt; dist[n - 1] &lt;&lt; &#x27; &#x27; &lt;&lt; ans.size() - 1 &lt;&lt; &#x27;
&#x27;; std::string s(n, &#x27;0&#x27;); for (int i = 1, len = ans.size(); i &lt; len; i++) &#123; s[ans[i - 1]] = &#x27;1&#x27;; std::cout &lt;&lt; s &lt;&lt; &#x27; &#x27; &lt;&lt; dist[ans[i]] - dist[ans[i - 1]] &lt;&lt; &#x27;
&#x27;; &#125;&#125;int main() &#123; std::ios::sync_with_stdio(false); std::cin.tie(nullptr); std::cout.tie(nullptr); solve(); return 0;&#125;

匿名函数，也叫 Lambda 表达式，是 C++ 11 提出的特性，匿名函数能使得我们的程序更加灵活轻便。 基本语法 1[capture](para) -&gt; returnType &#123; body &#125; capture ：闭包 para ：参数 returnType ：返回类型 body ：函数体 示例： 12auto fun = [](int x, int y) &#123; return x + y; &#125;;std::cout &lt;&lt; fun(1, 2) &lt;&lt; &#x27;
&#x27;; // 3 闭包 capture 称为闭包，作用是引用匿名函数体外的变量，其有如下格式： 12345678[] // 不引用外部变量，无法在函数体内使用外部变量[x, &amp;y] // x 按值捕获，y 按照引用捕获[&amp;] // 可以捕获所有外部变量，按照引用捕获[=] // 可以捕获所有外部变量，按照值捕获[&amp;, x] // x 按照值捕获，其余所有变量按照引用捕获[=, &amp;y] // y 按照引用捕获，其余所有变量按照值捕获[this] // 通过引用捕获当前对象[*this] // 通过传值的方式捕获当前对象 示例： 12345678910111213141516171819#include &lt;iostream&gt;#include &lt;vector&gt;int main() &#123; int sum = 0; std::vector&lt;int&gt; arr = &#123;1, 2, 3, 4, 5&#125;; auto fun = [&amp;sum](int x) &#123; sum += x; &#125;; // auto fun = [sum](int x) &#123; sum += x; &#125;; 报错，sum 必须是可修改的左值，故只能通过引用的方式捕获 for (int x : arr) &#123; fun(x); &#125; std::cout &lt;&lt; sum &lt;&lt; &#x27;
&#x27;; // 15 return 0;&#125; 运用 匿名函数的运用十分灵活，以下我们举几个常见的例子： 在 STL 的排序函数中自定义排序方式： 123456789101112131415161718192021222324252627#include &lt;iostream&gt;#include &lt;vector&gt;#include &lt;algorithm&gt;struct Student &#123; std::string name; int grade;&#125;;int main() &#123; std::vector&lt;Student&gt; s_arr = &#123;&#123;&quot;Jack&quot;, 99&#125;, &#123;&quot;Alice&quot;, 97&#125;, &#123;&quot;Bob&quot;, 83&#125;, &#123;&quot;Carl&quot;, 100&#125;, &#123;&quot;Clara&quot;, 66&#125;&#125;; std::sort(s_arr.begin(), s_arr.end(), [](const Student&amp; s1, const Student&amp; s2) &#123; return s1.grade &gt; s2.grade; &#125;); // 自定义学生成绩按照降序排序 for (const auto&amp; s : s_arr) &#123; std::cout &lt;&lt; s.name &lt;&lt; &#x27; &#x27; &lt;&lt; s.grade &lt;&lt; &#x27;
&#x27;; &#125; // Carl 100 // Jack 99 // Alice 97 // Bob 83 // Clara 66 return 0;&#125; 定义一个匿名函数作为参数的函数： 12345678910111213141516171819#include &lt;iostream&gt;double solve(std::function&lt;double(double)&gt; fn, double left, double right, int n) &#123; // 求一元函数在区间 [left, right] 的定积分 double res = -fn(left) + fn(right); double d = (right - left) / n; for (int i = 0; i &lt; n; i++) &#123; res += 2 * fn(left + i * d) + 4 * fn(left + (i + 0.5) * d); &#125; return d * res / 6;&#125;int main() &#123; std::cout &lt;&lt; solve([](double x) &#123; return x * x * x; &#125;, 0, 1, 10) &lt;&lt; &#x27;
&#x27;; // 0.25 return 0;&#125;
